import { StatusBar } from 'expo-status-bar';
import React,{useState,useRef, createRef} from 'react';
import { StyleSheet, Text,TextInput, View,Animated,TouchableOpacity } from 'react-native';
const fields = ['firstname', 'lastname', 'email', 'mobileno'];
export default function App() {
  //const value = useRef(new Animated.ValueXY({ x: 200, y: 500 })).current;
  const valuee = useState(new Animated.ValueXY({ x: 200, y: 500 }))[0];
  const ViewRefs = React.useRef([]);
  //ViewRefs.current=[];
  // if (ViewRefs.current.length != fields.length) {
  //   ViewRefs.current = fields.map((_, i) =>createRef(new Animated.Value(0)))

  // }
  var i = 0;
  while (i < fields.length) {
    console.log(i)
    if (i == 0) {
      ViewRefs.current[i]=(new Animated.Value(1))
      ViewRefs.current[(i + 1) * 858] = (new Animated.ValueXY({ x: 50, y: 100 }))
      ViewRefs.current[(i+5)*858]=(new Animated.Value(1))
    } else {
      ViewRefs.current[i]=(new Animated.Value(0))
      ViewRefs.current[(i + 1) * 858] = (new Animated.ValueXY({ x: 50, y: 100 }))
      ViewRefs.current[(i+5)*858]=(new Animated.Value(1))
    }
    i++;
  }

  console.log(ViewRefs)

  const fade = useRef(new Animated.Value(0)).current;
  var curr_i = 0;
  function moveText() {
    var index = curr_i
    if (index < fields.length ) {
      Animated.timing(ViewRefs.current[(index + 5) * 858], {
        toValue: 0.5,
        duration: 2000,
        useNativeDriver: false
      })
        .start()
    }
      Animated.timing(ViewRefs.current[(index + 1) * 858], {

        toValue: { x: 50, y: 150 },
        duration: 2000,
        useNativeDriver: false
      }).start(() => {

        if (index < fields.length - 1) {
          Animated.timing(ViewRefs.current[index + 1], {
            toValue: 1,
            duration: 3000,
            useNativeDriver: false
          }).start()
        }
      })




    curr_i++;
  }

  return (

    <View style={styles.container}>
      {
        fields.map((a, index) => (
          <Animated.View
            key={index}
            style={{
              position: "relative",
              left: ViewRefs.current[(index + 1) * 858].x, bottom: ViewRefs.current[(index + 1) * 858].y,
              opacity: ViewRefs.current[index],
              // transform: [
              //   {
              //     scale:ViewRefs.current[(index + 5) * 858].interpolate({
              //       inputRange: [0, 1],
              //       outputRange: [10, 10]
              //     })

              //   }
              // ]
              transform: [
                {
                  scaleX:ViewRefs.current[(index + 5) * 858]
                }
              ]
            }}
        >
          <TextInput 
           placeholder={fields[index]}
            style={{height: 40, borderColor: 'gray', border:"none",borderBottom:"1px solid black",outline:"none",width:"50%",justifyContent:"center" }}
            ref={ViewRefs.current[index]}
          />
          </Animated.View>


  ))
      }
       <TouchableOpacity  onPress={() => moveText()}  ><Text>Click e</Text></TouchableOpacity>
         </View>
  );
}

const styles = StyleSheet.create({
  container: {
    width: "100%",
    overflow:"scroll",
    textAlign: "center",
    height: "100%",
    textAlignVertical:"middle",
    justifyContent:"center"
  },
});
