import React,{useState} from 'react'
import { StyleSheet, Text, View,Button,TextInput } from 'react-native'
import axios from "axios";
import { GlobalContext } from "../context/globalState";
var setX = 100;
export default function Description() {
  const { user } = React.useContext(GlobalContext);
  const [description, setDescription] = useState({
    birthday: "",
    description: "",
    likes: "",
    dislikes: "",
  })
  React.useEffect(() => {
    setDescription({
      birthday: user.birthday,
      description: user.description,
      likes: user.likes,
      dislikes: user.dislikes,
    })
  })
    
    const updateDetails =async () => {
        await axios.post("http://192.168.0.106:3006/user/addDesc", description, {
            headers: {
                authorization: "Bearer " + localStorage.getItem("love_token")
            }
        }).then((res) => {
            console.log(res);
        })
    }
    return (
        <View>
         
        <View>
        <TextInput
          placeholder="Your Birthday ....."
          onChangeText={text => setDescription({ ...description, "birthday": text })}
       value={description.birthday}
          style={{
            height: 40, left: setX, marginTop: "10%", borderColor: 'gray', width: "60%", borderBottomWidth: 1,
            borderColor: "black",
          }}
            
        
          />
           <TextInput
          placeholder="Something about you ....."
          onChangeText={text => setDescription({ ...description, "description": text })}
          value={description.description}
          style={{
            height: 40, left: setX, marginTop: "10%", borderColor: 'gray', width: "60%", borderBottomWidth: 1,
            borderColor: "black",
          }}
            
        
          />
           <TextInput
          placeholder="Your likes ....."
          onChangeText={text => setDescription({ ...description, "likes": text })}
          value={description.likes}
          style={{
            height: 40, left: setX, marginTop: "10%", borderColor: 'gray', width: "60%", borderBottomWidth: 1,
            borderColor: "black",
          }}
            
        
          />
           <TextInput
          placeholder="Your dislikes ....."
          onChangeText={text => setDescription({ ...description, "dislikes": text })}
          value={description.dislikes}
          style={{
            height: 40, left: setX, marginTop: "10%", borderColor: 'gray', width: "60%", borderBottomWidth: 1,
            borderColor: "black",
          }}
            
        
                />
                <Button
                    title="submit"
                    onPress={() => {updateDetails()}}
                />
      </View>
      
        </View>
    )
}

const styles = StyleSheet.create({})
