import React,{useState,useContext} from 'react'
import {
    StyleSheet,
    Text,
    
    Animated, View, Image, PanResponder, FlatList, Dimensions, SafeAreaView
} from 'react-native'
import {GlobalContext} from "../context/globalState"
import * as Location from 'expo-location';
import { EvilIcons } from '@expo/vector-icons';
import axios from "axios";
//import Animated from 'react-native-reanimated';
import {
    FlingGestureHandler,
    Directions,
    State,
  } from 'react-native-gesture-handler';
import { Extrapolate } from 'react-native-reanimated';
const { width } = Dimensions.get('screen');
const OVERFLOW_HEIGHT = 70;
const SPACING = 10;
const ITEM_WIDTH = width * 0.76;
const ITEM_HEIGHT = ITEM_WIDTH * 1.7;
const VISIBLE_ITEMS = 3;
const SCREEN_HEIGHT = Dimensions.get('window').height
const SCREEN_WIDTH = Dimensions.get('window').width
var inl = 0;
export default function FindUsers({navigation}) {
   
 
    const [image, setImage] = useState();
    const [data, setData] = useState([])
    const { logout, user } = useContext(GlobalContext);
  
    React.useEffect(() => {
    
        async function fetchh() {
            await axios.get("http://192.168.0.106:3006/user/me", {
                headers: {
                    authorization: "Bearer " + localStorage.getItem("love_token")
                }
            }).then((res) => {
                if (res.data.user_.description == "") {
                    navigation.navigate("Description")
             
                } else {
                    loc()
                }
            })
        }
        fetchh()
    }, [])
    console.log(user)
   
    const names = ["PRIYAM", "PODDAR", "JHON", "DEO", "LUNEA"]
    const im = ["https://images.unsplash.com/photo-1593642533144-3d62aa4783ec?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1000&q=60",
        "https://images.unsplash.com/photo-1603489735710-e8ec2ceaa65a?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1000&q=60",
        "https://images.unsplash.com/photo-1603486001734-7d92cedd1d33?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1000&q=60",
        "https://images.unsplash.com/photo-1603472609666-69b4a338b0f2?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1000&q=60",
        "https://images.unsplash.com/photo-1603427867595-e33279138066?ixlib=rb-1.2.1&ixid=eyJhcHBfaWQiOjEyMDd9&auto=format&fit=crop&w=1000&q=60",
]
    
    async function loc() {
        let { status } = await Location.requestPermissionsAsync()
        if (status !== 'granted') {
          
            console.log('Permission to access location was denied');
        
        } else {
            const loc = await Location.getCurrentPositionAsync();

            var current_location = {
                // latitude: 12.899929,
                // longitude:77.510606
                latitude: loc.coords.latitude,
                longitude: loc.coords.longitude
            }
            console.log(current_location)
           // let result = await Location.reverseGeocodeAsync(current_location);
            await axios.get(`http://192.168.56.1:3006/api/nearby/${current_location.latitude}/${current_location.longitude}/560060`).then((res) => {
               // setImage(res.data.ne[0].user.image)
                setData(res.data.ne);
                
                console.log(res.data.ne[0].user.image)
            }).catch((err) => {
                console.log(err)
            })
         
        }
    }
    const scrollXIndex = React.useRef(new Animated.Value(0)).current;
    const scrollXAnimated = React.useRef(new Animated.Value(0)).current;
    const position = React.useRef(new Animated.ValueXY()).current;
    const [index, setIndex] = React.useState(0);
    
const panResponder= React.useRef(PanResponder.create({
    onStartShouldSetPanResponder: (evt, gestureState) => true,
    onPanResponderMove: (evt, gestureState) => {
        
          //console.log("dsbkj")
    position.setValue({x:gestureState.dx,y:gestureState.dy})
    },
    onPanResponderRelease: (evt, gestureState) => {
        if (gestureState.dx > 120) {
            Animated.spring(position, {
                toValue: { x: SCREEN_WIDTH + 100, y: gestureState.dy },
                useNativeDriver:true
            }).start(async () => {
            
                setIndex(++inl)
                console.log(index)
                if (inl == im.length) {
                    setIndex(0)
                    inl=0
                }
                })
            }
        else if (gestureState.dx <-120) {
            Animated.spring(position, {
                toValue: { x: -SCREEN_WIDTH - 100, y: gestureState.dy },
                useNativeDriver:true
            }).start(async () => {
               
                setIndex(++inl)
                console.log(index)
                if (inl == im.length) {
                    setIndex(0)
                    inl=0
                }
                
                })
            } else {
                Animated.spring(position, {
                  toValue: { x: 0, y: 0 },
                    friction: 4,
                  useNativeDriver:true
                }).start()
              }
        }
})).current;
    const setActiveIndex = React.useCallback((activeIndex) => {
        scrollXIndex.setValue(activeIndex);
        setIndex(activeIndex)
    })
    const rotateee = position.x.interpolate({
        inputRange: [-SCREEN_WIDTH / 2, 0, SCREEN_WIDTH / 2],
        outputRange: ['-10deg', '0deg', '10deg'],
        extrapolate: 'clamp'
    })
    const rotateAndTrans = { 
        transform: [{
            rotate:rotateee
        },
        ...position.getTranslateTransform()]
    }
    const likeOpacity = position.x.interpolate({
        inputRange: [-SCREEN_WIDTH / 2, 0, SCREEN_WIDTH / 2],
        outputRange: [0, 0, 1],
        extrapolate: 'clamp'
    })
    const dislikeOpacity = position.x.interpolate({
        inputRange: [-SCREEN_WIDTH / 2, 0, SCREEN_WIDTH / 2],
        outputRange: [1,0,0],
        extrapolate: 'clamp'
   })
   const nextCardOpacity = position.x.interpolate({
    inputRange: [-SCREEN_WIDTH / 2, 0, SCREEN_WIDTH / 2],
    outputRange: [1,0,1],
    extrapolate: 'clamp'
   })
   const nextCardScale = position.x.interpolate({
    inputRange: [-SCREEN_WIDTH / 2, 0, SCREEN_WIDTH / 2],
