import React, { useState, useRef, createRef } from 'react';
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import Home from "./screens/Home"
import { AntDesign } from '@expo/vector-icons';
import axios from "axios";
import { StyleSheet, Text, TextInput,View,Button,TouchableOpacity } from 'react-native';
import Login from "./screens/Login";
import Cameraa from "./screens/Cameraa"
import { GlobalContext } from "./context/globalState";
import FindUsers from "./screens/FindUsers"
export default function Index() {
    const { token, addToken } = React.useContext(GlobalContext);
    var token_ = token;
    React.useEffect(() => {
        addToken(localStorage.getItem("love_token"))

    },[])

  const Stack = createStackNavigator();
  const SignNaviator = createStackNavigator()

  React.useEffect(() => {
      token_ = localStorage.getItem("love_token");
      if (token_ != null) {
          axios.get("http://192.168.0.106:3006/user/me", {
              headers: {
                  authorization: token_
              }
          }).then((res) => {
              console.log(res.user_)
          })
      }
  }, [token])
  
  return (
    

      <NavigationContainer>
        {token_ != null ?
          <Stack.Navigator>
        
            
                  <Stack.Screen name="Home" component={Home} />
                  <Stack.Screen name="FindUsers" component={FindUsers} />
            <Stack.Screen name="Camera" component={Cameraa} />
          </Stack.Navigator> :
          <SignNaviator.Navigator>
            <SignNaviator.Screen name="Login" component={Login}/>
          </SignNaviator.Navigator>}
      </NavigationContainer>
      
  );
}

const styles = StyleSheet.create({
  container: {
    width: "100%",
    overflow:"scroll",
    textAlign: "center",
    height: "100%",
    marginTop:"40%",
        
  },
});
