import React, { useState, useRef, createRef } from 'react';
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import Home from "./screens/Home"
import { AntDesign } from '@expo/vector-icons';
import { StyleSheet, Text, TextInput,View,Button,TouchableOpacity } from 'react-native';
import Login from "./screens/Login";
import Cameraa from "./screens/Cameraa"
import {GlobalProvider} from "./context/globalState";
import FindUsers from "./screens/FindUsers"
export default function App (){

  const Stack = createStackNavigator();
  const SignNaviator = createStackNavigator()
  var token = localStorage.getItem("love_token");
  React.useEffect(() => {
    token = localStorage.getItem("love_token");
  },[localStorage.getItem("love_token")])
  return (
    <GlobalProvider>

      <NavigationContainer>

          <Stack.Navigator>
          {token != null ?
            <>
            <Stack.Screen name="FindUsers" component={FindUsers} />
            <Stack.Screen name="Home" component={Home} />
            <Stack.Screen name="Camera" component={Cameraa} /> </>:  <Stack.Screen name="Camera" component={Cameraa} />}
          </Stack.Navigator> :
          {/* <SignNaviator.Navigator>
            <SignNaviator.Screen name="Login" component={Login}/>
          </SignNaviator.Navigator>} */}
      </NavigationContainer>
      </GlobalProvider>
  );
}

const styles = StyleSheet.create({
  container: {
    width: "100%",
    overflow:"scroll",
    textAlign: "center",
    height: "100%",
    marginTop:"40%",

  },
});
