import React, { useState, useRef, createRef } from 'react';
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack';
import Home from "./screens/Home"
import { AntDesign } from '@expo/vector-icons';
import axios from "axios";
import { StyleSheet, Text, TextInput,View,Button,TouchableOpacity } from 'react-native';
import Login from "./screens/Login";
import Cameraa from "./screens/Cameraa"
import Description from "./screens/Description";
import { GlobalContext } from "./context/globalState";
import FindUsers from "./screens/FindUsers"
var token_ =localStorage.getItem("love_token") ;
export default function Index({navigation}) {
    const { token, addToken,updateUser } = React.useContext(GlobalContext);
  const Stack = createStackNavigator();
  const SignNaviator = createStackNavigator()

    React.useEffect(() => {
        console.log(localStorage)
        if (localStorage.getItem("love_token") === null) {
            
        } else {
            addToken(localStorage.getItem("love_token"))
            check()
        }
        console.log(token)
},[])
    async function check() {
        
        if (token_ != null) {
            
            await  axios.get("http://192.168.0.106:3006/user/me", {
                  headers: {
                      authorization: "Bearer " + token_
                  }
              }).then((res) => {
                  
                  console.log(res.data.user_)
                updateUser(token_, res.data.user_)
                
              })
          }
  }
  return (
    

      <NavigationContainer>
        {token != null ?
          <Stack.Navigator>
        
        <Stack.Screen name="FindUsers" component={FindUsers} />
                  <Stack.Screen name="Home" component={Home} />
                  <Stack.Screen name="Description" component={Description} />
                
            <Stack.Screen name="Camera" component={Cameraa} />
          </Stack.Navigator> :
          <SignNaviator.Navigator>
            <SignNaviator.Screen name="Login" component={Login}/>
          </SignNaviator.Navigator>}
      </NavigationContainer>
      
  );
}

const styles = StyleSheet.create({
  container: {
    width: "100%",
    overflow:"scroll",
    textAlign: "center",
    height: "100%",
    marginTop:"40%",
        
  },
});
