import { StatusBar } from 'expo-status-bar';
import React,{useState,useRef, createRef} from 'react';
import { StyleSheet, Text,TextInput, View,Animated,TouchableOpacity } from 'react-native';
const fields = ['firstname', 'lastname', 'email id', 'mobile number','password'];
export default function App() {
  const valuee = useState(new Animated.ValueXY({ x: 200, y: 500 }))[0];
  const [ViewRefs,setv] = useState([]);
  const [data, setData] = useState({
    firstname: "",
    lastname: "",
    email: "",
    mobileno: "",
    password:""
    })
   const [error, setError] = useState();
  var i = 0;
  while (i < fields.length) {
    if (i == 0) {
      ViewRefs[i]=(new Animated.Value(1))
      ViewRefs[(i + 1) * 858] = (new Animated.ValueXY({ x: 400, y:10 }))
      ViewRefs[(i+8)*858]=(new Animated.Value(1))
    } else {
      ViewRefs[i]=(new Animated.Value(0))
      ViewRefs[(i + 1) * 858] = (new Animated.ValueXY({ x: 400, y: 10 }))
      ViewRefs[(i+8)*858]=(new Animated.Value(1))
    }
    i++;
  }

  console.log(ViewRefs)


  var curr_i = 0;
  function moveText() {

    console.log(ViewRefs[curr_i])
    if (ViewRefs[curr_i].value != "") {
      console.log(data)


      var index = curr_i
      if (index < fields.length) {
        Animated.timing(ViewRefs[(index +8) * 858], {
          toValue: 0.8,
          duration: 2000,
          useNativeDriver: false
        }).start()
      }
      Animated.timing(ViewRefs[(index + 1) * 858], {

        toValue: { x: 400, y: 150 },
        duration: 2000,
        useNativeDriver: false
      }).start(() => {

        if (index < fields.length - 1) {
          Animated.timing(ViewRefs[index + 1], {
            toValue: 1,
            duration: 3000,
            useNativeDriver: false
          }).start()
        }
      })




      curr_i++;
    } else {
      //setError("Please enter the required field")
    }
  }

  return (

    <View style={styles.container}>
      {
        fields.map((a, index) => (
          <Animated.View
            key={index}
            style={{

              left: ViewRefs[(index + 1) * 858].x, bottom: ViewRefs[(index + 1) * 858].y,
              opacity: ViewRefs[index],
              transform: [
                {
                  scaleY:ViewRefs[(index +8) * 858]
                }
              ]
            }}
        >
          <TextInput 
              placeholder={fields[index]}
              name={fields[index]}
             onChange={e=>setData({...data,[e.target.placeholder]:e.target.value})}
            style={{height: 40, borderColor: 'gray', width:"20%", border:"none",borderBottom:"1px solid black",outline:"none" }}
            ref={ViewRefs[index]}
          />
          </Animated.View>



  ))
      }
         {error && <Text>{error}</Text>}
       <TouchableOpacity  onPress={() => moveText()}  ><Text>Click me</Text></TouchableOpacity>
         </View>
  );
}

const styles = StyleSheet.create({
  container: {
    width: "100%",
    overflow:"scroll",
    textAlign: "center",
    height: "100%",
    textAlignVertical:"middle",
    justifyContent:"center"
  },
});
