import React, { useState, useEffect } from 'react';
//import { StyleSheet, Text,Modal, View,TouchableOpacity } from 'react-native'
import axios from "axios";
import { Alert, Modal, StyleSheet, TouchableOpacity,Image,SafeAreaView, TextInput,Platform,useWindowDimensions, Text, TouchableHighlight,ScrollView, View, Button,KeyboardAvoidingView } from 'react-native';
import { Camera } from 'expo-camera';
import * as Location from 'expo-location';
import { Entypo } from '@expo/vector-icons';
import { Ionicons } from '@expo/vector-icons';
const Home = ({ route, navigation }) => {
  const [image, setImage] = useState(null);
 
  const [mood, setMood] = useState();
  const [reason, setReason] = useState();
  const [intrest, setIntrest] = useState();
  const [location, setLocation] = useState();
  const [address, setAddress] = React.useState({});
  const [modalVisible, setModalVisible] = React.useState(false);
  const windowHeight = useWindowDimensions().height;
  React.useEffect(() => {
    loc()
    if (route.params != undefined) {
    //  console.log(route.params.image)
      const ll = route.params.image;
      setImage(ll)
    }
   
    // this.setState({ locationResult: JSON.stringify(location) });
    
    // Center the map on the location we just fetched.
    //   this.setState({mapRegion: { latitude: location.coords.latitude, longitude: location.coords.longitude, latitudeDelta: 0.0922, longitudeDelta: 0.0421 }});
   
  }, [route.params]);
  async function loc() {
    let { status } = await Location.requestPermissionsAsync()
    if (status !== 'granted') {
      
      console.log('Permission to access location was denied');
    
    } else {
      const loc = await Location.getCurrentPositionAsync();
      setLocation(loc)
      var current_location = {
        // latitude: 12.899929,
        // longitude:77.510606
        latitude: loc.coords.latitude,
        longitude: loc.coords.longitude
      }
      let result = await Location.reverseGeocodeAsync(current_location);
      
      setAddress(
        result[0]
      )
      console.log(address)
     
    }
  }
  const rl = React.useRef();
  const [hasPermission, setHasPermission] = useState(null);
  const [type, setType] = useState(Camera.Constants.Type.back);
  // const [image, setImage] = useState();
  const [hasCamera, setHas] = useState(false);

  useEffect(() => {
    (async () => {
      const { status } = await Camera.requestPermissionsAsync();
      setHasPermission(status === 'granted');
    })();
  }, []);

  if (hasPermission === null) {
    return <View />;
  }
  if (hasPermission === false) {
    return <Text>No access to camera</Text>;
  }
 
  async function submit() {
    const data = {
      mood: mood,
      reason: reason,
      intrest: intrest,
      image:image,
      address: address,
      location: {
        latitude: location.coords.latitude,
        longitude: location.coords.longitude
      }
    }
    console.log(data)
    await axios.post("http://192.168.42.147:3006/api/upload", data).then((res) => {
      console.log(res.data)
    }).catch((err)=>console.log(err))
  }
  return (
    <KeyboardAvoidingView
          keyboardVerticalOffset = {500} // adjust the value here if you need more padding
        style = {{ flex: 1 }}
      behavior="padding" >
      <SafeAreaView style={[{ minHeight: Math.round(windowHeight) }]}>
<View>
     
        <Modal
        style={[{ minHeight: Math.round(windowHeight) }]}
        animationType="slide"
        transparent={true}
        visible={modalVisible}
        onRequestClose={() => {
          Alert.alert('Modal has been closed.');
        }}>
        <View style={styles.centeredView}>
          <View style={styles.modalView}>
                       
            <Text  onPress={() => {
          setModalVisible(false);
                }}>close</Text>
                {image == null ?
                  <TouchableOpacity onPress={() => {
                    setModalVisible(false)
                    navigation.navigate("Camera")
          
                  }}>
                    <Entypo name="camera" size={54} color="black" />
                  </TouchableOpacity> :
                  <Image source={image} style={{ width: 100, height: 100 }}></Image>}
                
                <TextInput
                  style={{ width: 200, borderColor: "black", marginTop: 20, borderBottomWidth: 1 }}
                  placeholder="About your current mood"
                onChangeText={text=>setMood(text)}
                />
                
                <TextInput
                  style={{ width: 200, borderColor: "black", marginTop: 20, borderBottomWidth: 1 }}
                  placeholder="Why are you here?"
                  onChangeText={text=>setReason(text)}/>
                <TextInput
                  style={{ width: 200, borderColor: "black", marginTop: 20, borderBottomWidth: 1 }}
                  placeholder="Intrests"
                  onChangeText={text=>setIntrest(text)}/>
                {address && (
                  <TextInput style={{ width: 200, borderColor: "black", marginTop: 20, borderBottomWidth: 1 }}
                    value={address.city}
                    placeholder="Location" />)}
                <Button
                  style={{marginTop:"10%"}}
                  title="click me" onPress={()=>submit()}/>
                        
          </View>
        </View>
      </Modal>
    
      
      {/* <Text>
        
      </Text> */}

     
     
          <TouchableHighlight
     
     onPress={() => {
       setModalVisible(true);
     }}>
     <Text>
       Open
       </Text>
          </TouchableHighlight>
          <TextInput style={{width:200,borderColor:"black",marginTop:20,borderBottomWidth:1}} placeholder="About your current mood" />
        </View>
     
     
      </SafeAreaView>
      </KeyboardAvoidingView>

    )
}

export default Home

const styles = StyleSheet.create({centeredView: {
    flex: 1,

    justifyContent: 'center',
    alignItems: 'center',
    marginTop: 22,
  },
  modalView: {
      margin: 20,
      width: "80%",
      height:"80%",
    backgroundColor: 'white',
    borderRadius: 20,
    padding: 5,
    alignItems: 'center',
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5,
  },
  openButton: {
    backgroundColor: '#F194FF',
    borderRadius: 20,
    padding: 10,
    elevation: 2,
  },
  textStyle: {
    color: 'white',
    fontWeight: 'bold',
    textAlign: 'center',
  },
  modalText: {
    marginBottom: 15,
    textAlign: 'center',
  },
});
