import React, { useState, useEffect } from 'react';
//import { StyleSheet, Text,Modal, View,TouchableOpacity } from 'react-native'

import { Alert, Modal, StyleSheet, TouchableOpacity,Image, TextInput, Text, TouchableHighlight, View } from 'react-native';
import { Camera } from 'expo-camera';
import * as Location from 'expo-location';
import { Entypo } from '@expo/vector-icons';
import { Ionicons } from '@expo/vector-icons';
const Cameraa = ({ navigation }) => {
  // const [address, setAddress] = React.useState({});
  const [modalVisible, setModalVisible] = React.useState(false);

  const rl = React.useRef();
  const [hasPermission, setHasPermission] = useState(null);
  const [type, setType] = useState(Camera.Constants.Type.front);
  

  useEffect(() => {
    (async () => {
      const { status } = await Camera.requestPermissionsAsync();
      setHasPermission(status === 'granted');
    })();
  }, []);

  if (hasPermission === null) {
    return <View />;
  }
  if (hasPermission === false) {
    return <Text>No access to camera</Text>;
  }
  const takePicture = async () => {
    
        const options = {quality: 1, base64: true,   aspect: [4, 3],
          quality: 1,};
        const data = await rl.current.takePictureAsync(options);
      
      navigation.navigate("Home",{image:data})
    
};
  return (
      
   
        <Camera style={{ flex: 1 }} type={type} ref={rl}>
          <View
            style={{
              flex: 1,
              backgroundColor: 'transparent',
              flexDirection: 'row',
            }}>
            <TouchableOpacity
              style={{
                flex: 0.1,
                alignSelf: 'flex-end',
                alignItems: 'center',
              }}
              onPress={() => {
                setType(
                  type === Camera.Constants.Type.back
                    ? Camera.Constants.Type.front
                    : Camera.Constants.Type.back
                );
              }}>
             <Ionicons name="ios-reverse-camera" size={4} color="black" />
            </TouchableOpacity>
            <TouchableOpacity
              style={{
                flex: 0.1,
                alignSelf: 'flex-end',
                alignItems: 'center',
              }}
              onPress={async () => {
                takePicture()
              }}>
              <Entypo name="document-landscape" size={54} color="black" />
              
            </TouchableOpacity>
          </View>
        </Camera> 
    

      
    

    )
}

export default Cameraa

const styles = StyleSheet.create({centeredView: {
    flex: 1,

    justifyContent: 'center',
    alignItems: 'center',
    marginTop: 22,
  },
  modalView: {
      margin: 20,
      width: "80%",
      height:"80%",
    backgroundColor: 'white',
    borderRadius: 20,
    padding: 5,
    alignItems: 'center',
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5,
  },
  openButton: {
    backgroundColor: '#F194FF',
    borderRadius: 20,
    padding: 10,
    elevation: 2,
  },
  textStyle: {
    color: 'white',
    fontWeight: 'bold',
    textAlign: 'center',
  },
  modalText: {
    marginBottom: 15,
    textAlign: 'center',
  },
});
