export default (state, action) => {
    switch (action.type) {
        case "ADD_USER":
            console.log(action.payload,"inactionnnnn")
        return {
          ...state,
          user: action.payload,
        };
        case "LOGOUT_USER":
            localStorage.removeItem("love_token")
        return {
          ...state,
          user: {},
          token:null
            };
        case "UPDATE_USER": 
            return {
                ...state,
                user: action.payload.user,
                token:action.payload.token
            }
        case "ADD_TOKEN": 
            localStorage.setItem("love_token",action.payload)
            return {
                ...state,
                token:action.payload
            }
      default:
        return;
    }
  };
